import React, { useState } from 'react';
import './PrediccionReserva.css';

function Prediccion() {
  const [formData, setFormData] = useState({
    adultos: '',
    ninos: '',
    precio: '',
    duracion: '',
    mes: '',
    antiguedad: ''
  });
  const [cargando, setCargando] = useState(false);
  const [resultado, setResultado] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setResultado('');
    setCargando(true);

    const { adultos, ninos, precio, duracion, mes, antiguedad } = formData;

    // Validaciones
    if ([adultos, ninos, precio, duracion, mes, antiguedad].some(valor => valor === '' || Number(valor) < 0)) {
      alert("Todos los campos son obligatorios y deben ser mayores o iguales a cero.");
      setCargando(false);
      return;
    }

    try {
      const response = await fetch('http://localhost:8080/api/prediccion', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          num_adultos: Number(adultos),
          num_ninos: Number(ninos),
          precio_habitacion: Number(precio),
          duracion_estancia: Number(duracion),
          mes_checkin: Number(mes),
          antiguedad_reserva: Number(antiguedad)
        })
      });

      if (!response.ok) throw new Error('Error en la respuesta del servidor');

      const data = await response.json();
      setResultado(data.prediccion ? 'S√≠' : 'No');
    } catch (error) {
      console.error(error);
      alert("Ocurri√≥ un error al hacer la predicci√≥n.");
    } finally {
      setCargando(false);
    }
  };

  return (
    <div className="prediccion-container">
      <h1 className="prediccion-title">üîç Predicci√≥n de Cancelaci√≥n de Reserva</h1>
      <form onSubmit={handleSubmit} className="prediccion-form">
        {['adultos', 'ninos', 'precio', 'duracion', 'mes', 'antiguedad'].map((campo, index) => (
          <div key={index}>
            <label htmlFor={campo}>{campo.charAt(0).toUpperCase() + campo.slice(1)}</label>
            <input
              type="number"
              id={campo}
              name={campo}
              min="0"
              value={formData[campo]}
              onChange={handleChange}
            />
          </div>
        ))}
        <div className="prediccion-button-container">
          <button className="prediccion-button" disabled={cargando}>
            {cargando ? (
              <>
                <span className="spinner"></span> Prediciendo...
              </>
            ) : 'Predecir'}
          </button>
        </div>
      </form>
      {resultado && (
        <div className="prediccion-resultado">
          <h2>Resultado:</h2>
          <p className={resultado === 'S√≠' ? 'resultado-si' : 'resultado-no'}>
            {resultado === 'S√≠'
              ? '‚ùå La reserva probablemente ser√° cancelada'
              : '‚úÖ La reserva probablemente se mantendr√°'}
          </p>
        </div>
      )}
    </div>
  );
}

export default PrediccionReserva;
