// Frontend en React para enviar datos al backend Spring con WEKA

import React, { useState } from 'react';
import axios from 'axios';

export default function App() {
  const [formData, setFormData] = useState({
    numAdultos: 2,
    numNinos: 0,
    tipoHabitacion: 'Doble',
    precioHabitacion: 150,
    duracionEstadia: 3,
    diaSemanaCheckIn: 'Monday',
    mesCheckIn: 4,
    antiguedadReserva: 30
  });

  const [resultado, setResultado] = useState(null);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:4040/api/prediccion', formData);
      setResultado(res.data);
    } catch (err) {
      console.error('Error en la predicción', err);
    }
  };

  return (
    <div className="p-4 max-w-xl mx-auto">
      <h1 className="text-xl font-bold mb-4">Predicción de Cancelación de Reserva</h1>
      <form onSubmit={handleSubmit} className="grid gap-4">
        <input name="numAdultos" type="number" placeholder="Adultos" value={formData.numAdultos} onChange={handleChange} className="p-2 border" />
        <input name="numNinos" type="number" placeholder="Niños" value={formData.numNinos} onChange={handleChange} className="p-2 border" />
        <select name="tipoHabitacion" value={formData.tipoHabitacion} onChange={handleChange} className="p-2 border">
          <option value="Suite">Suite</option>
          <option value="Doble">Doble</option>
          <option value="Simple">Simple</option>
        </select>
        <input name="precioHabitacion" type="number" placeholder="Precio" value={formData.precioHabitacion} onChange={handleChange} className="p-2 border" />
        <input name="duracionEstadia" type="number" placeholder="Duración" value={formData.duracionEstadia} onChange={handleChange} className="p-2 border" />
        <select name="diaSemanaCheckIn" value={formData.diaSemanaCheckIn} onChange={handleChange} className="p-2 border">
          <option>Monday</option>
          <option>Tuesday</option>
          <option>Wednesday</option>
          <option>Thursday</option>
          <option>Friday</option>
          <option>Saturday</option>
          <option>Sunday</option>
        </select>
        <input name="mesCheckIn" type="number" placeholder="Mes (1-12)" value={formData.mesCheckIn} onChange={handleChange} className="p-2 border" />
        <input name="antiguedadReserva" type="number" placeholder="Antigüedad" value={formData.antiguedadReserva} onChange={handleChange} className="p-2 border" />
        <button type="submit" className="bg-blue-500 text-white p-2 rounded">Predecir</button>
      </form>

      {resultado && (
        <div className="mt-4 p-4 bg-gray-100 rounded">
          <h2 className="text-lg font-semibold">Resultado:</h2>
          <p>{resultado === 'Si' ? '❌ La reserva probablemente será cancelada' : '✅ La reserva probablemente se mantendrá'}</p>
        </div>
      )}
    </div>
  );
}
