// src/pages/PrediccionPage.jsx
import React, { useState } from "react";
import { predecirCancelacion } from "../services/PrediccionService";

const PrediccionPage = () => {
  const [resultado, setResultado] = useState(null);
  const [loading, setLoading] = useState(false);

  const handlePredecir = async () => {
    const token = localStorage.getItem("token"); // Asegúrate de guardar el token al iniciar sesión

    const datos = {
      leadTime: 30,
      arrivalDateMonth: "May",
      staysInWeekendNights: 2,
      staysInWeekNights: 3,
      adults: 2,
      children: 1,
      babies: 0,
      previousCancellations: 0,
      bookingChanges: 1,
      depositType: "No Deposit",
      customerType: "Transient",
      reservedRoomType: "A",
      assignedRoomType: "A"
    };

    try {
      setLoading(true);
      const resultado = await predecirCancelacion(datos, token);
      setResultado(resultado);
    } catch (error) {
      alert("Error al obtener la predicción.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="p-4">
      <h1 className="text-xl font-bold mb-4">Predicción de Cancelación</h1>
      <button
        onClick={handlePredecir}
        className="bg-blue-500 text-white px-4 py-2 rounded"
        disabled={loading}
      >
        {loading ? "Procesando..." : "Predecir"}
      </button>

      {resultado && (
        <div className="mt-4 p-4 border rounded">
          <p><strong>¿Cancelará?</strong> {resultado.cancelara ? "Sí" : "No"}</p>
          <p><strong>Confianza:</strong> {Math.round(resultado.confianza * 100)}%</p>
        </div>
      )}
    </div>
  );
};

export default PrediccionPage;
